sourceCodeOfComponts.tab.astro:
---
import { cls } from "@/src/lib/utils";

interface Props {
    class?: string;
    selected?: boolean;
    for: string;
    group_name: string;
    variant?: "button" | "underline";
}

const { class: className, selected, for: htmlFor, variant = "button", group_name } = Astro.props as Props;
---

<label
    data-slot="tab"
    for={htmlFor}
    class={cls(
        "relative cursor-pointer  select-none text-sm font-medium transition-colors duration-200",
        variant === "button" && "px-4 [:has(+_input:checked)]:text-white [:has(+_input:checked)]:bg-primary/80 py-2 rounded-lg border  bg-muted hover:bg-accent/50 peer-checked:bg-accent",
        variant === "underline" && "pb-2 border-b-2 hover:border-b-accent [:has(+_input:checked)]:border-b-primary/80",
        className)}
>
    <slot />
</label>
<input
    id={htmlFor}
    checked={selected}
    name={group_name}
    type="radio"
    class="opacity-100 sr-only peer left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2"
/>

