sourceCodeOfComponts.workflow-item.astro:
---
import { cls } from "@/src/lib/utils";

interface Props {
    class?: string;
    disabled?: boolean;
}

const { class: className, disabled } = Astro.props as Props;
---

<div class={cls(
    "relative pl-2 pb-3 pt-0",
    "border-l-2 border-dashed  border-foreground/60 px-4 not-prose mx-3 last:border-0",
    disabled && "opacity-50 pointer-events-none line-through decoration-dashed",
    className)}>
    <div class="w-8 h-8 rounded-full [&_svg]:w-4 [&_svg]:h-4  [&_svg]:text-foreground border-2 flex items-center justify-center border-primary  absolute left-0 -translate-x-1/2 bg-background top-0">
     <slot name="wf-icon" />
    </div>
    <div class="pl-3">
        <slot />
    </div>
</div>

